import React, { useEffect, useState } from 'react';
import { useDispatch } from 'react-redux';
import * as actions from '../../store/actions';
import AddRowToTable from '../AddRowToTable/AddRowToTable';

const EditableTable = (props) => {
    let columnsName = [];
    let columns = null;
    useEffect(() => {
        columns = Object.keys(props.tableData[0]).map((columnName, index) => {
            return columnName;
        })
        columnsName = columns;

        console.log(props.tableData);
    }, [props.tableData])

    setColumnsName(columns);

    return (
        <div>
            <table style={{ width: "100%" }}>
                <thead>
                    <tr>
                        { // Render table column name 
                            columnsName.map((columnName, index) => {
                                return <th key={index}>{columnName}</th>
                            })}
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                    {// Render table content  
                        props.tableData.map((val, key) => {
                            return (
                                <tr key={key}>
                                    {Object.keys(val).map((keyName, index) => {
                                        return (<td key={key + " " + index}>{val[keyName]}</td>)
                                    })}
                                    <td>
                                        <button>Edit</button>
                                        <button>Delete</button>
                                    </td>
                                </tr>
                            )
                        })
                    }
                </tbody>
            </table>

            <AddRowToTable columnsName={props.columnsName}
            ></AddRowToTable>
        </div>
    )
}

export default EditableTable;
