import React, { useEffect, useState } from 'react';
import { useDispatch } from 'react-redux';
import * as actions from '../../store/actions';
import AddRowToTable from '../AddRowToTable/AddRowToTable';

const EditableTable = (props) => {
    const [tableData, setTableData] = useState([]);

    useEffect(() => {
        Server.fetchTableData('tabel_obat').then(res => setTableData(res.data));
    }, [])

    useEffect(() => {
      Server.fetchTableData(props.tableName).then(res => set_table_obat(res.data));
      console.log('use effect called');
    }, [props])

    let actionHeader = <th>Action</th>;

    let actionButtons = <td>
        {props.Editable}
        <button>Edit</button>
        <button>Delete</button>
    </td>

    let addButton = <AddRowToTable
        tableName={props.tableName}
        tableData={props.tableData}
    ></AddRowToTable>

    if (!props.Editable) {
        actionButtons = null;
        addButton = null;
        actionHeader = null;
    }

    return (
        <div>
            <table style={{ width: "100%" }}>
                <thead>
                    <tr>
                        { // Render table column name 
                            Object.keys(props.tableData[0]).map((columnName, index) => {
                                return <th key={index}>{columnName}</th>
                            })}
                        {actionHeader}
                    </tr>
                </thead>
                <tbody>
                    {// Render table content  
                        props.tableData.map((val, key) => {
                            return (
                                <tr key={key}>
                                    {Object.keys(val).map((keyName, index) => {
                                        return (<td key={key + " " + index}>{val[keyName]}</td>)
                                    })}
                                    {actionButtons}
                                </tr>
                            )
                        })
                    }
                </tbody>
            </table>
            {addButton}
        </div>
    )
}

export default EditableTable;
