import React from 'react';
import actions from '../../store/actions';
import { useDispatch } from 'react-redux';

const AddRowToTable = (props) => {
    const [addFields, setAddFields] = useState({});
    const [columnsName] = props.columnsName;

    return (
        <div>
            <h3>Add: </h3>
            <form>
                {columnsName.map((value, index) => {
                    return (
                        <input
                            key={index}
                            placeholder={value}
                            onChange={(event) => {
                                const value = event.target.value;
                                setAddFields((prevValue) => {
                                    return {
                                        ...prevValue,
                                        [props.columnsName[index]]: value,
                                    }
                                })
                            }}
                        ></input>
                    )
                })}
                <button onClick={(e) => {
                    e.preventDefault();
                    dispatch(actions.insertToSelectedTable(addFields))
                }}>Add</button>
            </form>
        </div>
    )
}

export default AddRowToTable;
