////#endregion

//#region Sync methods
export const prepareSelectedTable = () => {
    return {
        type: ON_TABLE_SELECTED,
    }
}
//#endregion

//#region Async methods
export const fetchSelectedTable = () => {
    return (dispatch) => {
        dispatch(prepareSelectedTable());
    }
}

export const deleteFromSelectedTable = (columnName, value) => {
    return (dispatch) => {
        dispatch(prepareSelectedTable());
    }
}

export const insertToSelectedTable = () => {
    return (dispatch) => {
        dispatch(prepareSelectedTable());
    }
}
//#endregion

//#region Actions name
export const ON_TABLE_SELECTED = "ON_TABLE_SELECTED";
//#endregion
