import axios from 'axios';

//#region Axios requests 
const deleteTable = (columnName, value) => {
    Axios.post("http://localhost:3001/delete",
        {
            columnName: columnName,
            value: value
        }
    ).then((response) => {
        getQueryResult();
    })

}

const insertQueryResult = (objectToAdd) => {
    Axios.post("http://localhost:3001/insert", objectToAdd).then((response) => {
        getQueryResult();
    })
}
//#endregion

//#region Sync methods
export const requestSelectedTable = () => {
    return {
        type: ON_TABLE_SELECTED,
    }
}
//#endregion

//#region Async methods
export const fetchSelectedTable = () => {
    return (dispatch) => {

        Axios.get("http://localhost:3001/getTable",).then((response) => {
            setQueryResult(response.data);

            let newColumns = [];
            Object.keys(response.data[0]).map((value, index) => {
                return newColumns.push(value);
            })
            setColumns(newColumns);
        }).catch((error) => {
            console.log(error);
        })
    }
}

export const deleteFromSelectedTable = (columnName, value) => {
    return (dispatch) => {
        dispatch(deleteTable(columnName, value));
    }
}

export const insertToSelectedTable = (objectToAdd) => {
    return (dispatch) => {
        dispatch(insertQueryResult(objectToAdd));
    }
}
//#endregion

//#region Actions name
export const REQUEST_SELECTED_TABLE = "REQUEST_SELECTED_TABLE";

export const FETCH_SELECTED_TABLE = "FETCH_SELECTED_TABLE";
export const ADD_TO_SELECTED_TABLE = 'ADD_TO_SELECTED_TABLE';
export const DELETE_FROM_SELECTED_TABLE = 'DELETE_FROM_SELECTED_TABLE';
//#endregion
