import './App.css';
import Axios from 'axios';
import React, { useState } from "react";

function App() {
  const [queryResult, setQueryResult] = useState([]); // Objec that contain all of query result (SELECT * FROM anggota)
  const [columns, setColumns] = useState([]); 
  const [tableData, setTableData] = useState([]);

  const getQueryResult = () => {
    Axios.get("http://localhost:3001/employees").then((response) => {
      const newResponse = response.data;
      setQueryResult(newResponse);
    })
  }

  const getColumns = () => { 
    const newColumns = [];
    setColumns = Object.keys(queryResult[0]).map((value, index) => {
        newColumns.push(value);
    })

    setColumns(newColumns);
    console.log(columns);
  }

  React.useEffect(() => {
    getQueryResult();
  }, [])

  return (
    <div className="App">
      <h1>Hello world</h1>
      <button onClick={getQueryResult}>Get Employee</button>
      {queryResult.map((val, id) => {
        return Object.keys(val).map((keyName, index) => {
          return <div>
            <p>{keyName}: {val[keyName]}</p>
          </div>
        })
      })}
    </div>
  );
}

export default App;
