import React, { useEffect, useState } from 'react';
import { useDispatch } from 'react-redux';
import * as actions from '../../store/actions';
import AddRowToTable from '../AddRowToTable/AddRowToTable';
import Server from '../../Axios/Server';

const EditableTable = (props) => {
    const [tableData, setTableData] = useState([{test: 'test', lala: 'lala'}]); 

    const update = () => {  
        Server.fetchTableData(props.tableName).then(res => setTableData(res.data));
    }

    // Start
    useEffect(() => {
        update();
        setNeedToUpdate(false);
    }, []) 

    let actionHeader = <th>Action</th>;

    let actionButtons = <td>
        {props.Editable}
        <button>Edit</button>
        <button>Delete</button>
    </td>

    let addButton = <AddRowToTable
        tableName={props.tableName}
        tableData={tableData} 
        onClick={update}
    ></AddRowToTable>

    if (!props.Editable) {
        actionButtons = null;
        addButton = null;
        actionHeader = null;
    }

    return (
        <div>
            <h3>{props.tableName}</h3>
            <table style={{ width: "100%" }}>
                <thead>
                    <tr>
                        { // Render table column name 
                            Object.keys(tableData[0]).map((columnName, index) => {
                                return <th key={index}>{columnName}</th>
                            })}
                        {actionHeader}
                    </tr>
                </thead>
                <tbody>
                    {// Render table content  
                        tableData.map((val, key) => {
                            return (
                                <tr key={key}>
                                    {Object.keys(val).map((keyName, index) => {
                                        return (<td key={key + " " + index}>{val[keyName]}</td>)
                                    })}
                                    {actionButtons}
                                </tr>
                            )
                        })
                    }
                </tbody>
            </table>
            {addButton}
        </div>
    )
}

export default EditableTable;
